import React, {useContext} from "react";
import { DragDropContext } from 'react-beautiful-dnd';
import { GlobalContext } from '../../store'
import components from './components';
const { Header, LeftSection, RightSection } = components;

getDraggedTweet = (draggedTweetId) => {
  const { tweets } = this.context;
  console.log(draggedTweetId, tweets, typeof draggedTweetId)
  return tweets.find((tweet)=>(String(tweet.id) === String(draggedTweetId)));
}

removeSavedTweet = (tweetIndex) => {
  try {
    const {removeSavedTweet} = this.context;
    removeSavedTweet(tweetIndex);
  } catch (e) {
    console.log('Something went wrong',e);
  }
}

onDragEnd = result => {
  try {
    const {setSavedTweets} = this.context;
    const { destination, draggableId } = result;
    // if dropable is invalid
    if (!destination) {
      return;
    }
    if(destination.droppableId === 'right-section' && draggableId){
      let draggedTweet = this.getDraggedTweet(draggableId);
      if( draggedTweet ) {
        setSavedTweets( draggedTweet );
      }
    }
  } catch (e) {
    console.log('Something went wrong',e);
  }
}

const Dashboard = () => {

  const { fetchTweets, tweets, savedTweets } = useContext(GlobalContext);
    return (
      <div className='container'>
        <Header></Header>
        <div className='tweet-container'>
        <DragDropContext onDragEnd={this.onDragEnd}>
          <LeftSection 
            fetchTweets={fetchTweets}
            tweets={tweets}
          />
          <div className='drag-arrow-container'>
            <span>Drag tweet</span>
            <i className='fa fa-arrow-right arrow-right'></i>
            <span>To save</span>
          </div>
          <RightSection
            savedTweets={savedTweets}
            removeSavedTweet={this.removeSavedTweet}
          />
        </DragDropContext>

        </div>
      </div>
    );
}
export default Dashboard;
